import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { supabase } from "./supabaseClient";

const ListProducts_SP = () => {
  const [listProduct, setListProduct] = useState([]);
  const navigate = useNavigate();

  // ðŸ§© Láº¥y dá»¯ liá»‡u sáº£n pháº©m tá»« Supabase
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const { data, error } = await supabase
          .from("product1")
          .select("*")
          .order("id", { ascending: true });

        if (error) throw error;
        setListProduct(data);
      } catch (err) {
        console.error("Lá»—i khi láº¥y dá»¯ liá»‡u:", err.message);
      }
    };
    fetchProducts();
  }, []);

  // ðŸ—‘ XÃ³a sáº£n pháº©m
  const handleDelete = async (id) => {
    if (window.confirm("Báº¡n cÃ³ cháº¯c muá»‘n xÃ³a sáº£n pháº©m nÃ y khÃ´ng?")) {
      try {
        const { error } = await supabase.from("product1").delete().eq("id", id);
        if (error) throw error;
        alert("âœ… ÄÃ£ xÃ³a sáº£n pháº©m!");
        setListProduct(listProduct.filter((item) => item.id !== id));
      } catch (err) {
        console.error("Lá»—i khi xÃ³a sáº£n pháº©m:", err.message);
      }
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Danh sÃ¡ch sáº£n pháº©m</h2>

      {/* âœ… NÃºt thÃªm sáº£n pháº©m */}
      <div style={{ marginBottom: "20px" }}>
        <button
          onClick={() => navigate("/addproduct")}
          style={{
            backgroundColor: "#007bff",
            color: "#fff",
            padding: "10px 16px",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
            fontWeight: "bold",
            boxShadow: "0 2px 5px rgba(0,0,0,0.2)",
          }}
        >
          âž• ThÃªm Sáº£n Pháº©m
        </button>
      </div>

      {/* ðŸ›’ Danh sÃ¡ch sáº£n pháº©m */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, minmax(220px, 1fr))",
          gap: "16px",
        }}
      >
        {listProduct.map((p) => (
          <div
            key={p.id}
            style={{
              border: "1px solid #ddd",
              borderRadius: "8px",
              padding: "10px",
              textAlign: "center",
              backgroundColor: "#fff",
              boxShadow: "0 2px 8px rgba(0,0,0,0.05)",
            }}
          >
            <img
              src={p.image}
              alt={p.title}
              style={{
                height: "180px",
                width: "100%",
                objectFit: "contain",
                cursor: "pointer",
              }}
              onClick={() => navigate(`/sanpham/${p.id}`)}
            />
            <h4 style={{ margin: "10px 0 5px" }}>{p.title}</h4>
            <p style={{ color: "#e63946", fontWeight: "bold" }}>${p.price}</p>

            {/* ðŸ—‘ XÃ³a sáº£n pháº©m */}
            <button
              onClick={() => handleDelete(p.id)}
              style={{
                marginTop: "8px",
                backgroundColor: "#dc3545",
                color: "#fff",
                border: "none",
                padding: "6px 12px",
                borderRadius: "5px",
                cursor: "pointer",
              }}
            >
              ðŸ—‘ Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ListProducts_SP;
