import "./styles.css";

// ðŸ“¦ Import cÃ¡c trang
import Home from "./Home";
import Layout from "./Layout";
import Trang1 from "./Trang1";
import Member from "./Member";
import ListProduct from "./ListProduct.js";
import ListProducts_SP from "./ListProducts_SP.js";
import Chitietsanpham from "./Chitietsanpham";
import ProductDetail from "./ProductDetail";
import AddProduct from "./AddProduct";
import LoginPage from "./LoginPage";
import LogoutPage from "./LogoutPage";
import ProtectedRoute from "./ProtectedRoute";
import ListProducts_SP_Admin from "./ListProducts_SP_Admin";
import EditProduct from "./EditProduct";

import { BrowserRouter, Routes, Route } from "react-router-dom";

// ðŸ§­ Cáº¥u hÃ¬nh router chÃ­nh
export default function App() {
  return (
    <BrowserRouter>
      <Routes>
        {/* Layout bao ngoÃ i */}
        <Route path="/" element={<Layout />}>
          {/* Trang chá»§ hiá»ƒn thá»‹ danh sÃ¡ch sáº£n pháº©m */}
          <Route index element={<ListProducts_SP />} />

          {/* CÃ¡c trang khÃ¡c */}
          <Route path="trang1" element={<Trang1 />} />
          <Route path="member" element={<Member />} />

          {/* Trang chi tiáº¿t sáº£n pháº©m */}
          <Route path="sanpham/:id" element={<ProductDetail />} />

          {/* Form thÃªm sáº£n pháº©m */}
          <Route path="addproduct" element={<AddProduct />} />

          {/* Form chá»‰nh sá»­a sáº£n pháº©m (admin) */}
          <Route path="admin/edit/:id" element={<EditProduct />} />

          {/* Trang Ä‘Äƒng nháº­p / Ä‘Äƒng xuáº¥t */}
          <Route path="login" element={<LoginPage />} />
          <Route path="logout" element={<LogoutPage />} />

          {/* Khu vá»±c quáº£n trá»‹ (chá»‰ admin truy cáº­p) */}
          <Route
            path="admin/products"
            element={
              <ProtectedRoute>
                <ListProducts_SP_Admin />
              </ProtectedRoute>
            }
          />
        </Route>
      </Routes>
    </BrowserRouter>
  );
}
