const Member = () => {
  const dssv = [
    {
      id: 1,
      hoten: "Nguyễn Trương Diễm Quỳnh",
      lop: "24140354",
      email: "quun.com",
      anh: "https://scontent.fsgn2-11.fna.fbcdn.net/v/t39.30808-1/465858055_1068801845053636_2588775470283495979_n.jpg?stp=dst-jpg_s200x200_tt6&_nc_cat=105&ccb=1-7&_nc_sid=1d2534&_nc_ohc=yYCf0HJBmuoQ7kNvwGSlVe9&_nc_oc=AdkKazB3Oleu6dUqyfDrFLvDMpX1pZCNii08N6zrTvKdbfyurqokCT2N2iqGPxzpRO4&_nc_zt=24&_nc_ht=scontent.fsgn2-11.fna&_nc_gid=lWxgpzauoMyQ1Xt7eG9Gmg&oh=00_AfgHTzD9O0uOGuszs-e2E4rIKI1Y5e7vVXiOhyj4Y12IUg&oe=6918411F",
    },
    {
      id: 2,
      hoten: "Diệp Yến Khoa",
      lop: "24150009",
      email: "khoa.com",
      anh: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTWbYiLT4quOyCRnpvukQueu4u810lP4QiPQg&s",
    },
    {
      id: 3,
      hoten: "Trần Thái Hòa",
      lop: "24140585",
      email: "hoa.com",
      anh: "data:image/jpeg;base64,",
    },
    {
      id: 4,
      hoten: "Phan Nguyễn Nhân Tâm",
      lop: "24150102",
      email: "tam.com",
      anh: "https://www.anhnghethuatdulich.com/wp-content/uploads/2025/09/chiikawa-anh-cuoi.jpg",
    },
  ];

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: "#f2f5ed", // nền đen sâu
        padding: "40px",
        fontFamily: "Inter, sans-serif",
        color: "#f5f5f5",
      }}
    >
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
          gap: "24px",
          width: "100%",
          maxWidth: "1000px",
        }}
      >
        {dssv.map((sv) => (
          <div
            key={sv.id}
            style={{
              backgroundColor: "#1c1c1e", // nền thẻ tối hơn
              borderRadius: "12px",
              overflow: "hidden",
              boxShadow: "0 4px 12px rgba(0,0,0,0.3)",
              transition: "transform 0.3s ease, box-shadow 0.3s ease",
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.transform = "scale(1.05)";
              e.currentTarget.style.boxShadow = "0 8px 20px rgba(0,0,0,0.5)";
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = "scale(1)";
              e.currentTarget.style.boxShadow = "0 4px 12px rgba(0,0,0,0.3)";
            }}
          >
            <img
              src={sv.anh}
              alt={sv.hoten}
              style={{
                width: "180%",
                height: "250px",
                objectFit: "cover",
              }}
            />
            <div style={{ padding: "15px" }}>
              <h3
                style={{
                  margin: "8px 0 4px",
                  fontSize: "16px",
                  color: "#ffffff",
                }}
              >
                {sv.hoten}
              </h3>
              <p
                style={{
                  margin: "4px 0",
                  color: "#cccccc",
                  fontSize: "14px",
                }}
              >
                {sv.lop}
              </p>
              <p
                style={{
                  margin: "0",
                  fontSize: "13px",
                  color: "#888888",
                }}
              >
                {sv.email}
              </p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Member;
